hotel_room_area = #{hotelRoomArea}, hotel_room_fee = #{hotelRoomFee} ,
hotel_initial_count=#{hotelInitialCount}, hotel_image=#{hotelImage}
where hotel_room_num =#{hotelRoomNum}
